1764964103110.oldValue:
async () => {
  // Lista de campos obrigat√≥rios
  const requiredFields = [
    { path: "sc.C2.forms.editChanges.userName", name: "Nome Completo" },
    { path: "sc.C2.forms.editChanges.userRg", name: "RG" },
    { path: "sc.C2.forms.editChanges.userPhone", name: "Telefone" },
    { path: "sc.C2.forms.editChanges.userAddress", name: "Endere√ßo" },
  ];

  // Fun√ß√£o auxiliar de leitura
  const getVal = (path) => {
    const val = tools.getCtData(path);
    if (Array.isArray(val)) return val[0] ?? "";
    return val ?? "";
  };

  // Valida√ß√£o ‚Äî log
  requiredFields.forEach(f => {
    console.log(f.name, "‚Üí", getVal(f.path));
  });

  // Checa campos vazios
  const emptyFields = requiredFields.filter((f) => {
    const v = getVal(f.path);
    return v === "" || v === null || v === undefined;
  });

  if (emptyFields.length > 0) {
    const msg = "Preencha os campos obrigat√≥rios.";
    tools.functions.setVar({
      args: "",
      pass: { keyPath: ["sc.C2.validationMessage"], value: [msg] },
    });

    console.warn("‚ö†Ô∏è Campos vazios:", emptyFields.map(f => f.name).join(", "));
    return;
  }

  tools.functions.setVar({
    args: "",
    pass: { keyPath: ["sc.C2.validationMessage"], value: ["Valida√ß√£o OK..."] },
  });

  console.log("üíæ Valida√ß√£o OK ‚Äî atualizando no Firebase...");

  // Inicializar Firebase
  let fbInit = tools.getCtData("all.temp.fireInit");
  if (!fbInit) {
    const { initializeApp, getApps } = await import("firebase/app");
    const cfg = tools.getCtData("all.temp.fireConfig");
    fbInit = getApps().length ? getApps()[0] : initializeApp(cfg);
    tools.setData({ path: "all.temp.fireInit", value: fbInit });
  }

  // Importa Firestore
  const { getFirestore, doc, updateDoc, serverTimestamp } = await import("firebase/firestore");
  const db = getFirestore(fbInit);

  // ID do documento a atualizar
  const docId = getVal("sc.C2.forms.editChanges.docId");

  if (!docId || typeof docId !== "string") {
    console.error("‚ùå docId inv√°lido:", docId);
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.validationMessage"],
        value: ["Erro: documento sem ID para atualizar."],
      },
    });
    return;
  }

  console.log("üìù Atualizando doc ID:", docId);

  // Monta dados atualizados
  const updatedData = {
    userName: getVal("sc.C2.forms.editChanges.userName"),
    userRg: getVal("sc.C2.forms.editChanges.userRg"),
    userPhone: getVal("sc.C2.forms.editChanges.userPhone"),
    userAddress: getVal("sc.C2.forms.editChanges.userAddress"),
    updatedAt: serverTimestamp(),
  };

  try {
    const ref = doc(db, "users", docId);

    await updateDoc(ref, updatedData);
    console.log("‚úÖ Documento atualizado!");

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.validationMessage"],
        value: ["Dados atualizados com sucesso!"],
      },
    });

  } catch (error) {
    console.error("‚ùå Erro ao atualizar:", error);
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.validationMessage"],
        value: ["Erro ao atualizar. Veja o console."],
      },
    });
    return;
  }

  // Limpa formul√°rio
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.C2.validationMessage"],
      value: [""],
    },
  });

  // Vai para tela
  tools.goTo("c5steps");
}

