  console.log({ data });
  data?.forEach((item: any) => {
    if (item?.stepId === "s1.1") {
        console.log({item})
      condMatch = true;
    }
  });

  const stlRadio = {
    borderRadius: 100,
    justifyContent: "center",
    alignItems: "center",
    width: 20,
    height: 20,
  };

  const iconActive = () => (
    <RN.View style={[{ backgroundColor: "green" }, stlRadio]}>
