  data?.forEach((d: any) => {
    if (d?.stepId === item.stepId) {
        console.log({d: d?.stepId , item: item.stepId});
      condMatch = true;
    }
  });

  const stlRadio = {
    borderRadius: 100,
    justifyContent: "center",
    alignItems: "center",
