async () => {
  // Lista de campos obrigat√≥rios
  const requiredFields = [
    { path: "sc.a1.iptChanges.condo", name: "Nome do Condom√≠nio" },
    { path: "sc.a1.iptChanges.address", name: "Endere√ßo" },
    { path: "sc.a1.iptChanges.startDate", name: "Data de In√≠cio" },
    { path: "sc.a1.iptChanges.endDate", name: "Data de Conclus√£o Prevista" },
    { path: "sc.a1.iptChanges.description", name: "Descri√ß√£o" },
  ];

  // Fun√ß√£o auxiliar para obter valor seguro
  const getVal = (path) => {
    const val = tools.getCtData(path);
    if (Array.isArray(val)) return val[0] ?? "";
    return val ?? "";
  };

  // Checa campos vazios
  const emptyFields = requiredFields.filter((f) => {
    const v = getVal(f.path);
    return v === "" || v === null || v === undefined;
  });

  // Define mensagem e estado final
  let message = "";

  if (emptyFields.length > 0) {
    message = `Preencha os campos obrigat√≥rios.`;

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: [message],
      },
    });

    console.warn("‚ö†Ô∏è Campos vazios detectados:", emptyFields.map(f => f.name).join(", "));
    return; // ‚ö†Ô∏è Interrompe o processo se houver campos vazios
  }

  // Se todos os campos estiverem preenchidos
  message = "‚úÖ Todos os campos foram preenchidos corretamente.";
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.a1.validationMessage"],
      value: [message],
    },
  });

  console.log("üíæ Valida√ß√£o OK ‚Äî salvando no Firebase...");

  // inicializar firebase
  let fbInit = tools.getCtData("all.temp.fireInit");
  if (!fbInit) {
    const { initializeApp, getApps } = await import("firebase/app");
    const cfg = tools.getCtData("all.temp.fireConfig");
    fbInit = getApps().length ? getApps()[0] : initializeApp(cfg);
    tools.setData({ path: "all.temp.fireInit", value: fbInit });
  }

  // Importa Firestore e salva o documento
  const { getFirestore, collection, addDoc, updateDoc, serverTimestamp } = await import("firebase/firestore");
  const db = getFirestore(fbInit);

  // Monta os dados a salvar
  const newDoc = {
    condo: getVal("sc.a1.iptChanges.condo"),
    address: getVal("sc.a1.iptChanges.address"),
    startDate: getVal("sc.a1.iptChanges.startDate"),
    endDate: getVal("sc.a1.iptChanges.endDate"),
    description: getVal("sc.a1.iptChanges.description"),
		images: getVal("sc.a1.iptChanges.arrImages"),
		files: getVal("sc.a1.iptChanges.arrImages"),
    createdAt: serverTimestamp(),
  };

  try {
    const docRef = await addDoc(collection(db, "condos"), newDoc);
    console.log("‚úÖ Documento salvo com ID:", docRef.id);

// Atualiza o documento para incluir o pr√≥prio ID
    await updateDoc(docRef, { docId: docRef.id });

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: ["üè¢ Condom√≠nio salvo com sucesso!"],
      },
    });
  } catch (error) {
    console.error("‚ùå Erro ao salvar documento:", error);
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: ["Erro ao salvar dados. Verifique o console."],
      },
    });
  }

//clean iptsChanges
tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.iptChanges"],
        value: [""],
      },
    });

//close Add
tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["all.toggles.a1.add"],
        value: [false],
      },
    });

//close sideRight
tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["all.toggles.sideRight"],
        value: [false],
      },
    });

//clean text message
tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: [""],
      },
    });
}

