1764266802161.oldValue:
async () => {
  const requiredFields = [
    { path: "sc.a1.editChanges.condo", name: "Nome do Condom√≠nio" },
    { path: "sc.a1.editChanges.address", name: "Endere√ßo" },
    { path: "sc.a1.editChanges.startDate", name: "Data de In√≠cio" },
    { path: "sc.a1.editChanges.endDate", name: "Data de Conclus√£o Prevista" },
    { path: "sc.a1.editChanges.description", name: "Descri√ß√£o" },
  ];

  const getVal = (path) => {
    const val = tools.getCtData(path);
    if (val === null || val === undefined) return "";
    if (Array.isArray(val)) return val[0] ?? "";
    return val;
  };

  const getArrayVal = (path) => {
    let val = tools.getCtData(path);
    if (!val) return []; // üîπ Retorna array vazio se n√£o houver nada
    if (Array.isArray(val)) return val;
    if (typeof val === "string") {
      try {
        const parsed = JSON.parse(val);
        return Array.isArray(parsed) ? parsed : [parsed];
      } catch {
        return [val]; // üîπ Se n√£o for JSON v√°lido, transforma em array simples
      }
    }
    return [val];
  };

  // üîπ Verifica campos obrigat√≥rios
  const emptyFields = requiredFields.filter((f) => {
    const v = getVal(f.path);
    return v === "" || v === null || v === undefined;
  });

  if (emptyFields.length > 0) {
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: ["Preencha os campos obrigat√≥rios."],
      },
    });
    console.warn("‚ö†Ô∏è Campos vazios detectados:", emptyFields.map(f => f.name).join(", "));
    return;
  }

  console.log("üíæ Valida√ß√£o OK ‚Äî atualizando documento no Firebase...");

  // üîπ Inicializa o Firebase
  let fbInit = tools.getCtData("all.temp.fireInit");
  if (!fbInit) {
    const { initializeApp, getApps } = await import("firebase/app");
    const cfg = tools.getCtData("all.temp.fireConfig") ?? {};
    fbInit = getApps().length ? getApps()[0] : initializeApp(cfg);
    tools.setData({ path: "all.temp.fireInit", value: fbInit });
  }

  const { getFirestore, doc, updateDoc, serverTimestamp } = await import("firebase/firestore");
  const db = getFirestore(fbInit);

  const docId = tools.getCtData("sc.a1.editChanges.docId");

  if (!docId || typeof docId !== "string") {
    console.error("‚ùå ID do documento inv√°lido:", docId);
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: ["ID do documento inv√°lido. N√£o foi poss√≠vel atualizar."],
      },
    });
    return;
  }

  // üîπ Monta o objeto para atualiza√ß√£o
  const updatedDoc = {
    condo: getVal("sc.a1.editChanges.condo"),
    address: getVal("sc.a1.editChanges.address"),
    startDate: getVal("sc.a1.editChanges.startDate"),
    endDate: getVal("sc.a1.editChanges.endDate"),
    description: getVal("sc.a1.editChanges.description"),
    images: getArrayVal("sc.a1.editChanges.images"), // üîπ Trata como array garantido
    files: getArrayVal("sc.a1.editChanges.documents"), // üîπ Trata como array garantido
    updatedAt: serverTimestamp(),
  };

  try {
    await updateDoc(doc(db, "condos", docId), updatedDoc);
    console.log("‚úÖ Documento atualizado com sucesso:", updatedDoc);

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: ["üè¢ Dados atualizados com sucesso!"],
      },
    });

    // üîπ Limpa formul√°rio e fecha pain√©is
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.editChanges"],
        value: [{}],
      },
    });

    //close Form
tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["all.toggles.forms"],
        value: [" "],
      },
    });

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["all.toggles.sideRight"],
        value: [false],
      },
    });
  } catch (error) {
    console.error("‚ùå Erro ao atualizar documento:", error);
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a1.validationMessage"],
        value: ["Erro ao atualizar os dados. Verifique o console."],
      },
    });
  }

  // üîπ Limpa mensagens ap√≥s o processo
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.a1.validationMessage"],
      value: [""],
    },
  });
}

