1761744805199.oldValue:
(value) => {
  // Garantir que o valor é string
  if (!value) value = "";

  // Remove tudo que não for número
  let cleaned = value.replace(/\D/g, "");

  // Limita a 8 dígitos (DDMMAAAA)
  if (cleaned.length > 8) cleaned = cleaned.slice(0, 8);

  // Adiciona as barras conforme o usuário digita
  let formatted = cleaned;
  if (cleaned.length > 4) {
    formatted = cleaned.replace(/^(\d{2})(\d{2})(\d{1,4}).*/, "$1/$2/$3");
  } else if (cleaned.length > 2) {
    formatted = cleaned.replace(/^(\d{2})(\d{1,2})/, "$1/$2");
  }

  // Atualiza a variável no contexto Flaxboll
  tools.setData({
    path: "sc.a1.iptChanges.startDate",
    value: formatted,
  });

  // Retorna o valor formatado (usado pelo input)
  return formatted;
}

