async () => {
  // Lista de campos obrigat√≥rios
  const requiredFields = [
    { path: "sc.C2.forms.iptsChanges.userName", name: "Nome Completo" },
    { path: "sc.C2.forms.iptsChanges.userRg", name: "RG" },
    { path: "sc.C2.forms.iptsChanges.userPhone", name: "Telefone" },
    { path: "sc.C2.forms.iptsChanges.userAddress", name: "Endere√ßo" },
  ];

  // Fun√ß√£o auxiliar para obter valor seguro
  const getVal = (path) => {
    const val = tools.getCtData(path);
    if (Array.isArray(val)) return val[0] ?? "";
    return val ?? "";
  };

  requiredFields.forEach((f) => {
    console.log(f.name, "‚Üí", getVal(f.path));
  });

  // Checa campos vazios
  const emptyFields = requiredFields.filter((f) => {
    const v = getVal(f.path);
    return v === "" || v === null || v === undefined;
  });

  // Define mensagem e estado final
  let message = "";

  if (emptyFields.length > 0) {
    message = `Preencha os campos obrigat√≥rios.`;

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.validationMessage"],
        value: [message],
      },
    });

    console.warn(
      "Campos vazios detectados:",
      emptyFields.map((f) => f.name).join(", ")
    );
    return; // ‚ö†Ô∏è Interrompe o processo se houver campos vazios
  }

  // Se todos os campos estiverem preenchidos
  message = "Campos preenchidos corretamente.";
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.C2.validationMessage"],
      value: [message],
    },
  });

  console.log("üíæ Valida√ß√£o OK ‚Äî salvando no Firebase...");

  // inicializar firebase
  let fbInit = tools.getCtData("all.temp.fireInit");
  if (!fbInit) {
    const { initializeApp, getApps } = await import("firebase/app");
    const cfg = tools.getCtData("all.temp.fireConfig");
    fbInit = getApps().length ? getApps()[0] : initializeApp(cfg);
    tools.setData({ path: "all.temp.fireInit", value: fbInit });
  }

  // Importa Firestore e salva o documento
  const { getFirestore, collection, doc, updateDoc, serverTimestamp } =
    await import("firebase/firestore");
  const db = getFirestore(fbInit);

  // Monta os dados a salvar
  const dataToUpdate = {
    userName: getVal("sc.C2.forms.iptsChanges.userName"),
    userRg: getVal("sc.C2.forms.iptsChanges.userRg"),
    userPhone: getVal("sc.C2.forms.iptsChanges.userPhone"),
    userAddress: getVal("sc.C2.forms.iptsChanges.userAddress"),

    fullRegister: true,
    updatedAt: serverTimestamp(),
  };

  try {
    const authUser = tools.getCtData("all.authUser");
    const uid = authUser.docId;
    console.log({ authUser });
    const refColl = collection(db, "users");
    const docRef = await doc(refColl, uid);

    console.log("‚úÖ Documento atualizado:", { dataToUpdate });

    // Atualiza o documento
    await updateDoc(docRef, dataToUpdate);

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.validationMessage"],
        value: ["Documento salvo com sucesso!"],
      },
    });
  } catch (error) {
    console.error("‚ùå Erro ao salvar documento:", error);
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.validationMessage"],
        value: ["Erro ao salvar dados. Verifique o console."],
      },
    });
  }

  clean iptsChanges
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.C2.forms.iptsChanges"],
      value: [""],
    },
  });

  //clean validationMessage
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.C2.validationMessage"],
      value: [" "],
    },
  });

  // Redireciona para tela "c5steps"
  tools.goTo("c5steps");
