export const Login = (props: TProps) => {
  if (RN.Platform.OS === "web") {
    return <LoginWeb pass={props?.pass} />;
  }
  return <LoginNative pass={props?.pass} />;
};

const setUserDB = async (user: any, authFromLogin?: Auth) => {
  console.log("Início SetUserDB - Parametro recebido", user);

  // Importa Firestore e salva o documento
  const { displayName, email, photoURL, uid } = user;
  let userToSet: Record<string, any> = {};

  const userData = {
    userName: displayName,
    userEmail: email,
    userImage: photoURL,
    docId: uid,
  };

  const {
    getFirestore,
    collection,
    doc,
    setDoc,
    updateDoc,
    serverTimestamp,
    getDoc,
    getDocs,
    query,
    where,
  } = await import("firebase/firestore");

  const fbApp = authFromLogin?.app ?? getFirebaseApp();
  if (!fbApp) {
    return { status: "error", message: "Firebase nao inicializado." };
  }
  const db = getFirestore(fbApp);

  let lotId = null;
  let condoId = null;

  // ---- LOTS
  const lotsCol = collection(db, "lots");
  const q = query(lotsCol, where("userEmail", "==", email));
  const searchLot = await getDocs(q);

  const lotExists = searchLot.docs.length > 0;
  if (lotExists) {
    const lotDoc = searchLot.docs[0];
    const lotData = lotDoc.data();
    console.log("Lote encontrado para o usuario:", lotData);

    lotId = lotDoc.id;
    condoId = lotData.condoId;
  } else {
    console.log("Nenhum lote encontrado para o usuario.");
  }

  // ---- PREREGISTERED USERS
  // ---- BARRAR SE ADM NÃO ADICIONOU O USUÁRIO ANTES
  const preRegCol = collection(db, "preRegisteredUsers");
  const q2 = query(preRegCol, where("userEmail", "==", email));
  const searchPreReg = await getDocs(q2);
  const preRegExists = searchPreReg.docs.length > 0;

  // SE pre-registrado existe, cria/atualiza user
  if (preRegExists) {
    const preRegDoc = searchPreReg.docs[0];
    const preRegData = preRegDoc.data();
    console.log("Pre-registro encontrado para o usuario:", preRegData);

    // ---- USERS
    const usersCol = collection(db, "users");
    const userDocRef = doc(usersCol, uid);
    const userDocSnap = await getDoc(userDocRef);
    console.log("Xx userDocSnap:", "users", uid);

    const userExists = userDocSnap.exists();

    console.log("Xx userDocSnap:", userExists);

    if (userExists) {
      const fullRegister = userDocSnap.data().fullRegister;

      userToSet = {
        updatedAt: serverTimestamp(),
        ...userData,

        fullRegister,
        condoId: condoId ?? null,
        lotId: lotId ?? null,
      };

      // Atualiza documento existente
      await updateDoc(userDocRef, userToSet);
      console.log("Usuario atualizado no banco de dados.");
    }

    if (!userExists) {
      userToSet = {
        createdAt: serverTimestamp(),
        ...userData,

        typeAccount: "app",
        fullRegister: false,
        condoId: condoId ?? null,
        lotId: lotId ?? null,
        steps: {},
      };
      console.log("Novo usuario adicionado ao banco de dados.");
      await setDoc(userDocRef, userToSet);
    }

    // Atualiza lote com ownerId
    if (lotId) {
      const lotDocRef = doc(lotsCol, lotId);
      await updateDoc(lotDocRef, { ownerId: uid });
    }

    // Fallback
    return { status: "success", data: userToSet };
  } else {
    console.log("Nenhum pre-registrado encontrado para o usuario.");

    return {
      status: "error",
      data: null,
      message: "Usuario não cadastrado. Contate o administrador.",
    };
  }
};

