async () => {
  const requiredFields = [
    {
      path: "sc.A7.forms.iptsChanges.partnerName",
      name: "Nome do Proprietário",
    },
    { path: "sc.A7.forms.iptsChanges.partnerMail", name: "E-mail" },
    { path: "sc.A7.forms.iptsChanges.lot", name: "Obra" },
    { path: "sc.A7.forms.iptsChanges.area", name: "Área" },
    { path: "sc.A7.forms.iptsChanges.totalValue", name: "Valor total da obra" },
    {
      path: "sc.A7.forms.iptsChanges.firstInstallment",
      name: "Valor total da entrada",
    },
    { path: "sc.A7.forms.iptsChanges.startDate", name: "Data de início" },
    { path: "sc.A7.forms.iptsChanges.endDate", name: "Data de término" },
  ];

  const getVal = (path) => {
    const val = tools.getCtData(path);
    if (Array.isArray(val)) return val[0] ?? "";
    return val ?? "";
  };

  const emptyFields = requiredFields.filter((f) => {
    const v = getVal(f.path);
    return v === "" || v === null || v === undefined;
  });

  if (emptyFields.length > 0) {
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a7.validationMessage"],
        value: ["Preencha os campos obrigatórios."],
      },
    });

    // estado = erro (COR VERMELHA)
    tools.setData({
      path: "sc.a7.validationColor",
      value: "red",
    });

    console.warn("Campos vazios:", emptyFields.map((f) => f.name).join(", "));
    return;
  }

  // estado = sucesso (COR VERDE)
  tools.setData({
    path: "sc.a7.validationColor",
    value: "green",
  });

  console.log("💾 Iniciando salvamento no Firebase...");

  // Inicializa o Firebase se ainda não estiver pronto
  let fbInit = tools.getCtData("all.temp.fireInit");
  if (!fbInit) {
    const { initializeApp, getApps } = await import("firebase/app");
    const cfg = tools.getCtData("all.temp.fireConfig");
    fbInit = getApps().length ? getApps()[0] : initializeApp(cfg);
    tools.setData({ path: "all.temp.fireInit", value: fbInit });
  }

  const {
    getFirestore,
    collection,
    doc,
    setDoc,
    addDoc,
    updateDoc,
    query,
    where,
    getDocs,
    serverTimestamp,
  } = await import("firebase/firestore");

  const db = getFirestore(fbInit);

  // Pega dados do formulário
  const email = getVal("sc.A7.forms.iptsChanges.partnerMail");
  const name = getVal("sc.A7.forms.iptsChanges.partnerName");
  const condoId = getVal("sc.A7.forms.currData.condoData.docId");

  if (!condoId) {
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a7.validationMessage"],
        value: ["⚠️ Selecione um condomínio antes de salvar."],
      },
    });
    console.warn("Condomínio ausente.");
    return;
  }

  // Busca usuário existente
  const usersRef = collection(db, "users");
  const q = query(usersRef, where("userEmail", "==", email));
  const querySnapshot = await getDocs(q);

  let ownerId;
  let userExists = false;

  if (!querySnapshot.empty) {
    userExists = true;
    const userDoc = querySnapshot.docs[0];
    ownerId = userDoc.id;
    console.log("✅ Usuário existente encontrado:", ownerId);
  } else {
    console.log("🆕 Criando novo usuário...");
    const newUserRef = doc(collection(db, "preRegisteredUsers"));
    // ownerId = newUserRef.id;

    await setDoc(newUserRef, {
      createdAt: serverTimestamp(),
      typeAccount: "app",
      userEmail: email,
      userName: name,
    });

    console.log("Novo usuário criado com docId:", ownerId);
  }

  // Cria o lote vinculado ao proprietário
  const dataSet = tools.getCtData("sc.A7.forms.iptsChanges");
  const condDataSet = Object.keys(dataSet).length > 0 ? dataSet : {};

  const lotsRef = collection(db, "lots");
  const newLot = {
    ...condDataSet,

    owner: name,
    userEmail: email,
    ownerId: "",
    condoId: condoId,
    createdAt: serverTimestamp(),
    installments: {},
    receipts: {},
  };

  try {
    const docRef = await addDoc(lotsRef, newLot);
    await updateDoc(docRef, { docId: docRef.id });

    console.log("Lote salvo com ID:", docRef.id);

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a7.validationMessage"],
        value: [
          userExists
            ? "Lote vinculado ao proprietário existente!"
            : "Proprietário e lote cadastrados com sucesso!",
        ],
      },
    });
  } catch (error) {
    console.error("❌ Erro ao salvar lote:", error);
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a7.validationMessage"],
        value: ["❌ Erro ao salvar os dados. Verifique o console."],
      },
    });
    return;
  }

  // Limpa campos e fecha painéis
  tools.functions.setVar({
    args: "",
    pass: { keyPath: ["sc.A7.forms.iptsChanges"], value: [""] },
  });

  //close Form
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["all.toggles.forms"],
      value: [" "],
    },
  });

  tools.functions.setVar({
    args: "",
    pass: { keyPath: ["all.toggles.sideRight"], value: [false] },
  });
  tools.functions.setVar({
    args: "",
    pass: { keyPath: ["sc.a7.validationMessage"], value: [""] },
  });
}

