(txt) => {
  try {
    if (typeof txt !== "string") txt = String(txt ?? "");

    // ───────────────────────────────
    // Mantém apenas números
    // ───────────────────────────────
    let clean = "";
    for (let i = 0; i < txt.length; i++) {
      const ch = txt[i];
      if (ch >= "0" && ch <= "9") clean += ch;
    }

    // Limita a 9 dígitos do RG
    if (clean.length > 9) clean = clean.slice(0, 9);

    console.log({ clean });

    // ───────────────────────────────
    // Montagem da máscara RG: XX.XXX.XXX-X
    // ───────────────────────────────
    let masked = "";

    if (clean.length > 0) masked = clean.slice(0, 2); // XX
    if (clean.length >= 3) masked += "." + clean.slice(2, 5); // XX.XXX
    if (clean.length >= 6) masked += "." + clean.slice(5, 8); // XX.XXX.XXX
    if (clean.length === 9) masked += "-" + clean.slice(8, 9); // XX.XXX.XXX-X

    console.log({ masked });

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.forms.iptsChanges.userRg"],
        value: [String(masked)],
      },
    });
  } catch (e) {
    console.error("Erro na máscara de RG:", e);
    return txt;
  }
}

(txt) => {
  try {
    if (typeof txt !== "string") txt = String(txt ?? "");

    // ───────────────────────────────
    // Mantém apenas números (sem regex)
    // ───────────────────────────────
    let clean = "";
    for (let i = 0; i < txt.length; i++) {
      const ch = txt[i];
      if (ch >= "0" && ch <= "9") {
        clean += ch;
      }
    }

    // Limita a 11 dígitos (telefone BR)
    if (clean.length > 11) clean = clean.slice(0, 11);

    // ───────────────────────────────
    // Montagem da máscara de telefone
    // ───────────────────────────────
    let masked = "";
    const len = clean.length;

    if (len > 0) masked = "(" + clean.slice(0, 2);   // "(" + D1 D2
    if (len >= 3) masked += ") " + clean.slice(2, 7); // ) + espaço + até 5 dígitos seguintes
    if (len >= 8) {
      // separa parte inicial + hífen
      const firstPart = clean.length === 11
        ? clean.slice(2, 7)     // 5 dígitos (celular)
        : clean.slice(2, 6);    // 4 dígitos (fixo)

      const lastPart = clean.slice(firstPart.length + 2);

      masked = "(" + clean.slice(0, 2) + ") " + firstPart;

      if (lastPart.length > 0) masked += "-" + lastPart;
    }

    console.log({ clean, masked });

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.forms.iptsChanges.userPhone"],
        value: [String(masked)],
      },
    });

  } catch (e) {
    console.error("Erro na máscara de telefone:", e);
    return txt;
  }
}

