};
const stlClose: ViewStyle = {
  ...stlAllCenter,
  width: 22,
  height: 22,
  borderRadius: 3,
  position: 'absolute',
  top: 2,
  right: 4,
};
const stlTxtClose: TextStyle = {
  lineHeight: 1,
  color: 'black',
  fontSize: 12,
};
const stlFillWidth: ViewStyle = { width: '100%' };
const stlsUser: any = {
  width: '100%',
  // borderBottomColor: '#ccc',
  borderBottomWidth: 1,
  paddingVertical: 4,
  paddingHorizontal: 4,
  fontSize: 12,
};
if (Platform.OS === 'web') stlsUser.outline = 'none';

