function checkUrl(string: string) {
  try {
    new URL(string);
    return true;
  } catch (err) {
    return false;
  }
}
const defaultUri =
  'https://www.budgetbatteries.com.au/wp-content/uploads/2021/12/Budget-batteries-placeholder.jpg';

// Element Image
export const ImageBox = (props: Tprops) => {
  // ------- set Props
  const { elementsProperties, styles, URIvariablePath, args } = props.pass;
  console.log({ URIvariablePath });

  // ---------- set Url Value as a single string
  let pathOrUri = URIvariablePath.join();

  const { condChildren, newArgChildren } = testArgs([pathOrUri], args);

  if (condChildren === 'arg') pathOrUri = newArgChildren;
  if (condChildren === 'var') {
    const joinedChld = pathOrUri.replace('$var_', '');
    console.log({ joinedChld });
    pathOrUri = useData(ct => pathSel(ct, joinedChld));
  }

  // Se for uma URL válida, usa diretamente, senão busca no useData
  const watchData = useData(ct => {
    if (checkUrl(pathOrUri)) return pathOrUri; // Se for uma URL, usa diretamente
    return pathSel(ct, pathOrUri); // Caso contrário, busca do caminho de dados
  });

  // ---------- set Styles
  const stl = getStlValues(styles);

  // ------- set User Element Properties (If Exists)
  const userElProps: any = {};
  for (let strObj of elementsProperties) {
    if (!strObj || typeof strObj !== 'string') continue;

    console.log('TEXT', { strObj });
    const parsedObject = JSON5.parse(strObj);

    for (const keyProp in parsedObject) {
      const valueProp = parsedObject[keyProp];
      const [hasVar, varValue] = getVarValue(valueProp, 'Component');
      userElProps[keyProp] = hasVar ? varValue : valueProp;
    }
  }

  console.log({ watchData });
  const isUrl = checkUrl(newArgChildren);
  console.log({ isUrl });

  const condFinalURI = isUrl ? newArgChildren : watchData || defaultUri;

  console.log({ condFinalURI });

  const allProps = {
    source: { uri: condFinalURI },
    style: [stl],
    resizeMode: 'cover',
    ...userElProps,
  };

  return <Image {...allProps} />;
};

const findFlatItem = obj => {
  if (typeof obj !== 'object' || obj === null) return null;

  if ('item' in obj) return obj.item;

  for (const key in obj) {
    if (Array.isArray(obj[key])) {
      for (const element of obj[key]) {
        const found = findFlatItem(element);
        if (found) return found;
      }
    } else if (typeof obj[key] === 'object') {
      const found = findFlatItem(obj[key]);
      if (found) return found;
    }
  }
  return null;
};

const testArgs = (children, args) => {
  let condChildren = '';
  let newArgChildren = 'undefined';

  const joinedChild = children.join();
  if (joinedChild.includes('$var_')) condChildren = 'var';
  if (joinedChild.includes('$arg_')) condChildren = 'arg';

  if (condChildren === 'arg') {
    const key = joinedChild.split('_')[1];
    console.log('TEXT', { key });

    const foundItem = findFlatItem(args);
    if (foundItem && key in foundItem) {
      newArgChildren = foundItem[key];
      console.log('TEXT', { newArgChildren });
    }
  }

  if (newArgChildren === 'undefined') console.log('ARG NOT FOUND');

  return { condChildren, newArgChildren };
};

// src/components/InputPicker.tsx
import JSON5 from 'json5';
import React, {
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from 'react';
import {
  View,
  Text,
  Pressable,
  TextInput,
  FlatList,
  Modal,
  Platform,
  StyleProp,
  ViewStyle,
  TextStyle,
  useWindowDimensions,
  Keyboard,
} from 'react-native';

import { pathSel } from '../project';
import { useData } from '../../..';

type Item = {
  label: string;
  value: string | number | null;
  disabled?: boolean;
};

type InputPickerProps = {
  pathItems: string; // onde estão as opções
  pathValue?: string; // (opcional) onde está o valor selecionado
  placeholder?: string;
  disabled?: boolean;
  searchable?: boolean;

  style?: StyleProp<ViewStyle>;
  inputStyle?: StyleProp<TextStyle>;
  dropdownStyle?: StyleProp<ViewStyle>;
  itemStyle?: StyleProp<ViewStyle>;
  itemTextStyle?: StyleProp<TextStyle>;

  emptyText?: string;
  maxVisibleItems?: number; // default 6
  itemHeight?: number; // default 44
  testID?: string;
};

type Tprops = {
  pass: {
    args: any;
    configs: string[]; // configs[0] = JSON5<InputPickerProps>
    // aceita (value, item, args) ou (value, args)
    arrFuncs?: Array<
      (value: Item['value'], item?: Item | null, args?: any) => void
    >;
  };
};

const EMPTY_ITEMS: Item[] = Object.freeze([]);
const isUnset = (v: any) => v === null || v === undefined || v === '';

// ---- zIndex global (WEB): garante que o picker aberto fica por cima
let __Z_INDEX_COUNTER__ = 1000;
// opcional: garante que só exista 1 picker aberto por vez (WEB)
let __CLOSE_LAST_OPEN__: null | (() => void) = null;

export const IptPicker: React.FC<Tprops> = props => {
  const { configs, arrFuncs = [], args } = props.pass;

  const obj0 = JSON5.parse(configs[0] || '{}') as Partial<InputPickerProps>;
  const {
    placeholder = 'Selecionar...',
    maxVisibleItems = 6,
    searchable = true,
    style,
    inputStyle,
    dropdownStyle,
    itemStyle,
    itemTextStyle,
    emptyText = 'Nada encontrado',
    itemHeight = 44,
    pathItems = '',
    pathValue,
    disabled = false,
    testID,
  } = obj0;

  const { height: winH } = useWindowDimensions();
  const [open, setOpen] = useState(false);
  const [query, setQuery] = useState('');
  const [hoverIndex, setHoverIndex] = useState<number>(-1);
  const wrapRef = useRef<View>(null);
  const inputRef = useRef<TextInput>(null);
  const listRef = useRef<FlatList<Item>>(null);

  // posição/tamanho do wrapper (medida sob demanda)
  const [wrapLayout, setWrapLayout] = useState<{
    y: number;
    x: number;
    h: number;
  }>({
    y: 0,
    x: 0,
    h: 0,
  });

  // z-index dinâmico (web)
  const [zTop, setZTop] = useState<number>(0);

  // ----- Data bindings
  const items: Item[] =
    useData((ct: any) =>
      pathItems ? (pathSel(ct, pathItems) as Item[]) : EMPTY_ITEMS,
    ) || EMPTY_ITEMS;

  const selectedValueFromStore: Item['value'] = useData((ct: any) =>
    pathValue ? (pathSel(ct, pathValue) as Item['value']) : null,
  );

  // estado local como fallback e para refletir seleção imediatamente
  const [selectedLocal, setSelectedLocal] = useState<Item['value'] | null>(
    isUnset(selectedValueFromStore) ? null : selectedValueFromStore,
  );

  // sincroniza do store apenas quando ele tiver um valor "definido"
  useEffect(() => {
    if (!pathValue) return;
    if (!isUnset(selectedValueFromStore))
      setSelectedLocal(selectedValueFromStore);
  }, [pathValue, selectedValueFromStore]);

  // valor efetivo a exibir
  const effectiveValue = pathValue
    ? !isUnset(selectedValueFromStore)
      ? selectedValueFromStore
      : selectedLocal
    : selectedLocal;

  // emissor para arrFuncs (2 ou 3 parâmetros)
  const emitChange = useCallback(
    (val: Item['value'], it: Item | null) => {
      for (const fn of arrFuncs) {
        if (typeof fn !== 'function') continue;
        try {
          fn(val, it, args);
        } catch (err) {
          console.error('[IptPicker] erro no handler:', err);
        }
      }
    },
    [arrFuncs, args],
  );

  const currentItem = useMemo(
    () => items.find(it => it.value === effectiveValue) ?? null,
    [items, effectiveValue],
  );

  const normalized = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return items;
    return items.filter(it => (it.label ?? '').toLowerCase().includes(q));
  }, [items, query]);

  const visibleCount = Math.min(normalized.length || 1, maxVisibleItems);
  const listHeight = visibleCount * itemHeight;

  const openUpwards = useMemo(() => {
    const spaceBelow = winH - (wrapLayout.y + wrapLayout.h);
    return spaceBelow < listHeight + (searchable ? 44 : 0) + 8;
  }, [winH, wrapLayout, listHeight, searchable]);

  // ----- Medição sob demanda (opção B)
  const measureWrapper = useCallback(() => {
    const node: any = wrapRef.current;

    if (node?.measureInWindow) {
      node.measureInWindow((x: number, y: number, w: number, h: number) => {
        setWrapLayout(prev =>
          prev.y === y && prev.x === x && prev.h === h ? prev : { y, x, h },
        );
      });
      return;
    }

    const el = node?._node ?? node;
    if (el?.getBoundingClientRect) {
      const r = el.getBoundingClientRect();
      setWrapLayout(prev =>
        prev.y === r.top && prev.x === r.left && prev.h === r.height
          ? prev
          : { y: r.top, x: r.left, h: r.height },
      );
    }
  }, []);

  // função para fechar (usada no “apenas um aberto por vez”)
  const closeSelf = useCallback(() => {
    setOpen(false);
    setQuery('');
    setHoverIndex(-1);
  }, []);

  const commitSelection = useCallback(
    (item: Item | null) => {
      const nextVal = item ? item.value : null;
      setSelectedLocal(nextVal); // reflete imediatamente
      emitChange(nextVal, item); // propaga pra fora
      closeSelf(); // fecha e limpa
    },
    [emitChange, closeSelf],
  );

  const toggleOpen = useCallback(() => {
    if (disabled) return;
    setOpen(p => !p);

    if (!open) {
      requestAnimationFrame(() => measureWrapper());
      if (searchable) setTimeout(() => inputRef.current?.focus(), 10);
    }
  }, [disabled, open, searchable, measureWrapper]);

  // z-index dinâmico + apenas 1 aberto por vez (WEB)
  useEffect(() => {
    if (Platform.OS !== 'web') return;

    if (open) {
      setZTop(++__Z_INDEX_COUNTER__);
      // fecha o último aberto (se existir e não for este)
      if (__CLOSE_LAST_OPEN__ && __CLOSE_LAST_OPEN__ !== closeSelf) {
        __CLOSE_LAST_OPEN__();
      }
      __CLOSE_LAST_OPEN__ = closeSelf;
    }

    return () => {
      // se este era o último aberto, limpa o apontador global
      if (__CLOSE_LAST_OPEN__ === closeSelf) __CLOSE_LAST_OPEN__ = null;
    };
  }, [open, closeSelf]);

  // remeasure enquanto aberto em resize/scroll no web
  useEffect(() => {
    if (Platform.OS !== 'web' || !open) return;

    const onResize = () => measureWrapper();
    const onScroll = () => measureWrapper();

    window.addEventListener('resize', onResize);
    window.addEventListener('scroll', onScroll, true);
    return () => {
      window.removeEventListener('resize', onResize);
      window.removeEventListener('scroll', onScroll, true);
    };
  }, [open, measureWrapper]);

  // click fora (web)
  useEffect(() => {
    if (Platform.OS !== 'web' || !open) return;

    const onDocMouseDown = (ev: any) => {
      const root = (wrapRef.current as any)?._node ?? (wrapRef.current as any);
      if (root && ev.target instanceof Node && !root.contains(ev.target)) {
        closeSelf();
      }
    };
    document.addEventListener('mousedown', onDocMouseDown);
    return () => document.removeEventListener('mousedown', onDocMouseDown);
  }, [open, closeSelf]);

  // fechar no back (Android) / Esc (web)
  useEffect(() => {
    if (!open) return;

    if (Platform.OS === 'web') {
      const onKey = (e: KeyboardEvent) => {
        if (e.key === 'Escape') closeSelf();
      };
      window.addEventListener('keydown', onKey);
      return () => window.removeEventListener('keydown', onKey);
    } else {
      const sub = Keyboard.addListener('keyboardDidHide', () => {});
      return () => sub.remove();
    }
  }, [open, closeSelf]);

  // navegação por teclado (web)
  useEffect(() => {
    if (Platform.OS !== 'web' || !open) return;

    const scrollIntoView = (idx: number) => {
      try {
        listRef.current?.scrollToIndex({
          index: idx,
          viewPosition: 0.5,
          animated: false,
        });
      } catch {}
    };

    const onKey = (e: KeyboardEvent) => {
      if (['ArrowDown', 'ArrowUp', 'Enter'].includes(e.key)) e.preventDefault();

      if (e.key === 'ArrowDown') {
        setHoverIndex(i => {
          const next = Math.min((i < 0 ? -1 : i) + 1, normalized.length - 1);
          scrollIntoView(next);
          return next;
        });
      } else if (e.key === 'ArrowUp') {
        setHoverIndex(i => {
          const next = Math.max(i - 1, 0);
          scrollIntoView(next);
          return next;
        });
      } else if (e.key === 'Enter') {
        const item = normalized[hoverIndex];
        if (item && !item.disabled) commitSelection(item);
      }
    };

    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, [open, normalized, hoverIndex, commitSelection]);

  const renderItem = ({ item, index }: { item: Item; index: number }) => {
    const isSelected = effectiveValue === item.value;
    const isHover = index === hoverIndex;
    const isDisabled = !!item.disabled;

    return (
      <Pressable
        onPress={() => !isDisabled && commitSelection(item)}
        onHoverIn={() => Platform.OS === 'web' && setHoverIndex(index)}
        disabled={isDisabled}
        accessibilityRole="button"
        accessibilityState={{ disabled: isDisabled, selected: isSelected }}
        style={[
          {
            height: itemHeight,
            paddingHorizontal: 12,
            justifyContent: 'center',
            backgroundColor: isHover ? '#f2f2ff' : '#fff',
            opacity: isDisabled ? 0.5 : 1,
            borderBottomWidth: 1,
            borderBottomColor: '#eee',
          },
          itemStyle,
        ]}
      >
        <Text
          style={[
            { fontSize: 14, color: '#111' },
            isSelected && { fontWeight: '600' },
            itemTextStyle,
          ]}
        >
          {item.label}
        </Text>
      </Pressable>
    );
  };

  const keyExtractor = (it: Item) => String(it.value);

  const DropdownContent = (
    <View
      style={[
        {
          width: '100%',
          maxHeight: listHeight + (searchable ? 44 : 0) + 8,
          borderRadius: 10,
          borderWidth: 1,
          borderColor: '#dcdcdc',
          backgroundColor: '#fff',
          overflow: 'hidden',
          shadowColor: '#000',
          shadowOpacity: 0.15,
          shadowOffset: { width: 0, height: 6 },
          shadowRadius: 12,
          elevation: 8,
        },
        dropdownStyle,
      ]}
    >
      {searchable && (
        <View
          style={{
            padding: 8,
            borderBottomWidth: 1,
            borderBottomColor: '#efefef',
          }}
        >
          <TextInput
            ref={inputRef}
            value={query}
            onChangeText={t => {
              setQuery(t);
              setHoverIndex(t ? 0 : -1);
              // não chamar handlers aqui – evita sobrescrever o valor no store
            }}
            placeholder="Buscar…"
            style={
              {
                height: 36,
                paddingHorizontal: 10,
                borderWidth: 1,
                borderColor: '#e2e2e2',
                borderRadius: 8,
                fontSize: 14,
                outlineStyle: Platform.OS === 'web' ? 'none' : undefined,
              } as any
            }
            {...(Platform.OS === 'web'
              ? { 'aria-label': 'Campo de busca do seletor', role: 'searchbox' }
              : {})}
          />
        </View>
      )}
      {normalized.length === 0 ? (
        <View
          style={{
            height: listHeight,
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Text style={{ color: '#666', fontSize: 14 }}>{emptyText}</Text>
        </View>
      ) : (
        <FlatList
          ref={listRef}
          data={normalized}
          keyExtractor={keyExtractor}
          renderItem={renderItem}
          getItemLayout={(_, index) => ({
            index,
            length: itemHeight,
            offset: index * itemHeight,
          })}
          style={{ maxHeight: listHeight }}
          keyboardShouldPersistTaps="handled"
        />
      )}
    </View>
  );

  // o input só considera prop disabled (itens desabilitados não travam o input)
  const inputIsDisabled = !!disabled;

  return (
    <View
      ref={wrapRef}
      style={[
        { position: 'relative' },
        // no web, quando aberto, joga o wrapper pro topo também
        Platform.OS === 'web' && open ? { zIndex: zTop } : null,
        style,
      ]}
      {...(Platform.OS === 'web'
        ? { role: 'combobox', 'aria-expanded': open, 'data-testid': testID }
        : {})}
    >
      <Pressable
        onPress={toggleOpen}
        disabled={inputIsDisabled}
        accessibilityRole="button"
        accessibilityState={{ disabled: inputIsDisabled, expanded: open }}
        style={[
          {
            minHeight: 44,
            paddingHorizontal: 12,
            borderWidth: 1,
            borderColor: inputIsDisabled ? '#e0e0e0' : '#cfcfcf',
            backgroundColor: inputIsDisabled ? '#f7f7f7' : '#fff',
            borderRadius: 10,
            justifyContent: 'center',
          },
          inputStyle as any,
        ]}
      >
        <Text style={{ color: currentItem ? '#111' : '#777', fontSize: 14 }}>
          {currentItem?.label ?? placeholder}
        </Text>
        <View
          style={{
            position: 'absolute',
            right: 10,
            top: 0,
            bottom: 0,
            justifyContent: 'center',
          }}
        >
          <Text style={{ fontSize: 14 }}>{open ? '▴' : '▾'}</Text>
        </View>
      </Pressable>

      {/* WEB: dropdown absoluto com zIndex dinâmico */}
      {Platform.OS === 'web' && open && (
        <View
          pointerEvents="box-none"
          style={{
            position: 'absolute',
            left: 0,
            right: 0,
            zIndex: zTop || 1, // dinâmico: o aberto ganha o maior z-index
            top: openUpwards ? undefined : wrapLayout.h + 6,
            bottom: openUpwards ? wrapLayout.h + 6 : undefined,
          }}
        >
          {DropdownContent}
        </View>
      )}

      {/* NATIVE: Modal de tela cheia */}
      {Platform.OS !== 'web' && (
        <Modal
          visible={open}
          transparent
          animationType="fade"
          onRequestClose={closeSelf}
        >
          <Pressable
            onPress={closeSelf}
            style={{
              flex: 1,
              backgroundColor: 'rgba(0,0,0,0.15)',
              justifyContent: openUpwards ? 'flex-end' : 'flex-start',
            }}
          >
            <View
              style={{
                paddingHorizontal: 12,
                paddingTop: openUpwards ? 0 : 8,
                paddingBottom: openUpwards ? 8 : 0,
              }}
            >
              {DropdownContent}
            </View>
          </Pressable>
        </Modal>
      )}
    </View>
  );
};

import React from 'react';
import JSON5 from 'json5';
import { Pressable, View, Text } from 'react-native';

export const Accordion = props => {
  const { configs, arrElements } = props.pass ?? {};

  // parse seguro (string/objeto/array)
  const safeParse = v => {
    if (!v) return {};
    if (typeof v === 'string') {
      try {
        return JSON5.parse(v) || {};
      } catch {
        return {};
      }
    }
    return typeof v === 'object' ? v || {} : {};
  };

  const cfg0 = Array.isArray(configs)
    ? safeParse(configs[0])
    : safeParse(configs);
  const items = Array.isArray(arrElements) ? arrElements : [];

  const clamp = (n, min, max) => Math.max(min, Math.min(max, n));

  const [openIdx, setOpenIdx] = React.useState(() => {
    if (!items.length) return null;
    // troque 0 por null se quiser iniciar fechado quando não vier defaultOpenIdx
    let idx = Number.isInteger(cfg0?.defaultOpenIdx) ? cfg0.defaultOpenIdx : 0;
    if (idx == null) return null;
    return clamp(idx, 0, items.length - 1);
  });

  const handleToggle = idx => {
    setOpenIdx(prev => (prev === idx ? null : idx));
  };

  // Garante que cada entrada vire um React element válido
  const asChild = (Item, idx) => {
    try {
      if (typeof Item === 'function') {
        const out = Item();
        if (React.isValidElement(out)) return out;
        if (typeof out === 'string' || typeof out === 'number')
          return <Text>{String(out)}</Text>;
        console.warn(
          'Accordion: função no idx %s não retornou elemento válido.',
          idx,
          out,
        );
        return null;
      }
      if (React.isValidElement(Item)) return Item;
      if (typeof Item === 'string' || typeof Item === 'number')
        return <Text>{String(Item)}</Text>;
      console.warn('Accordion: item inválido no idx %s.', idx, Item);
      return null;
    } catch (e) {
      console.error('Accordion: erro ao renderizar item %s ->', idx, e);
      return null;
    }
  };

  console.log('Accordion', { props, openIdx, cfg0 });
  const userStyles = cfg0?.styles ?? {};

  return (
    <>
      {items.map((Item, idx) => {
        const isOpen = openIdx === idx;
