() => {
  const arrImages =
    useData((ct) => ct.sc?.C6?.forms?.editChanges?.arrImages) ?? [];

  const getFileNameFromUrl = (url?: string, fallback = "arquivo") => {
    if (!url) return fallback;
    try {
      const lastChunk = url.split("/").pop() || fallback;
      return lastChunk.split("?")[0] || fallback;
    } catch {
      return fallback;
    }
  };

  const resolveFileName = (url?: string, fileName?: string) => {
    // base inicial: usa fileName se veio do Firestore, sen√£o extrai da URL
    let base =
      (fileName && fileName.trim()) || getFileNameFromUrl(url, "arquivo");

    // tenta achar extens√£o j√° presente no nome
    let ext = "";
    const nameExtMatch = base.match(/\.(jpg|jpeg|png|pdf)$/i);
    if (nameExtMatch) {
      ext = nameExtMatch[0].toLowerCase();
    }

    // se n√£o tiver extens√£o no nome, tenta pela URL
    if (!ext && url) {
      const urlExtMatch = url.match(/\.(jpg|jpeg|png|pdf)/i);
      if (urlExtMatch) ext = urlExtMatch[0].toLowerCase();
    }

    const cleanFileName = (txt = "") => {
      let out = "";

      for (let i = 0; i < txt.length; i++) {
        const ch = txt[i];

        // 1. Trocar "%2F" por "_"
        if (
          ch === "%" &&
          txt[i + 1] === "2" &&
          (txt[i + 2] === "F" || txt[i + 2] === "f")
        ) {
          out += "_"; // substitui por _
          i += 2; // pula "2F"
          continue;
        }

        // 2. Trocar "/" por "_"
        if (ch === "/") {
          out += "_";
          continue;
        }

        // 3. Permitir somente letras, n√∫meros, "_", "-", "."
        const isLetter = (ch >= "a" && ch <= "z") || (ch >= "A" && ch <= "Z");
        const isNumber = ch >= "0" && ch <= "9";
        const isAllowed = ch === "_" || ch === "-" || ch === ".";

        if (isLetter || isNumber || isAllowed) {
          out += ch;
        } else {
          out += "_"; // substitui caracteres inv√°lidos
        }
      }

      return out;
    };

    // normaliza nome (remove subpastas e caracteres ruins)
    base = cleanFileName(base);

    // fallback de extens√£o
    if (!ext) ext = ".jpg";

    if (!base.toLowerCase().endsWith(ext)) {
      base += ext;
    }

    return { name: base, ext }; // ex: { name: "images_1764_0___.jpg", ext: ".jpg" }
  };

  const { width } = RN.useWindowDimensions();
  const isSmall = width < 200;
  const itemWidth = isSmall ? "100%" : "48%";

  const styles = RN.StyleSheet.create({
    container: {
      flexDirection: "row",
      flexWrap: "wrap",
      justifyContent: "space-between",
      padding: 16,
    },
    img: {
      width: "100%",
      height: 55,
      borderRadius: 8,
      marginBottom: 10,
    },
    pressable: {
      width: itemWidth,
      marginBottom: 16,
    },
  });

  // --------- Native (Android / iOS) ----------
  const handleDownloadNative = async (url: string, fileName?: string) => {
    try {
      if (!url) {
        RN.Alert.alert("Arquivo inv√°lido", "URL n√£o encontrada.");
        return;
      }

      const { name, ext } = resolveFileName(url, fileName);
      const baseDir = FileSystem.documentDirectory + "downloads/";

      // garante diret√≥rio
      const dirInfo = await FileSystem.getInfoAsync(baseDir);
      if (!dirInfo.exists) {
        await FileSystem.makeDirectoryAsync(baseDir, { intermediates: true });
      }

      const fileUri = baseDir + name;
      console.log("Baixando para:", fileUri);

      const result = await FileSystem.downloadAsync(url, fileUri);
      console.log("Download conclu√≠do:", result.uri);

      // IMAGENS -> tenta salvar na GALERIA
      if (ext === ".jpg" || ext === ".jpeg" || ext === ".png") {
        try {
          await MediaLibrary.saveToLibraryAsync(result.uri);
          RN.Alert.alert("Sucesso", `Imagem salva na galeria como ${name}`);
          return;
        } catch (e) {
          console.log(
            "Erro ao salvar na galeria, fazendo fallback para share:",
            e
          );
          // se n√£o conseguir salvar na galeria, cai no share sheet
          if (await Sharing.isAvailableAsync()) {
            await Sharing.shareAsync(result.uri);
            return;
          }
        }
      }

      // OUTROS ARQUIVOS (PDF, etc) -> SHARE SHEET
      if (await Sharing.isAvailableAsync()) {
        await Sharing.shareAsync(result.uri);
      } else {
        RN.Alert.alert("Download conclu√≠do", "Arquivo salvo em: " + result.uri);
      }
    } catch (err) {
      console.log("Erro no download:", err);
      RN.Alert.alert("Erro", "N√£o foi poss√≠vel baixar o arquivo.");
    }
  };

  // --------- Web ----------
  const handleDownloadWeb = async (url: string, fileName?: string) => {
    if (!url) return;

    const { name } = resolveFileName(url, fileName);

    try {
      // baixa o arquivo como blob
      const response = await fetch(url);
      const blob = await response.blob();

      // cria uma URL tempor√°ria para o blob
      const blobUrl = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = blobUrl;
      link.download = name; // for√ßa o nome do arquivo no download

      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

