      borderColor: "#ccc",
      alignItems: "center",
      padding: 8,
    },
    txt: {
      fontSize: 12,
      color: "#666",
    },
  });

  const cleanFileName = (txt = "") => {
    let out = "";

    for (let i = 0; i < txt.length; i++) {
      const ch = txt[i];

      // Detecta "%2F"
      if (
        ch === "%" &&
        txt[i + 1] === "2" &&
        (txt[i + 2] === "F" || txt[i + 2] === "f")
      ) {
        out += "_";
        i += 2;
        continue;
      }

      // Barra vira "_"
      if (ch === "/") {
        out += "_";
        continue;
      }

      // Permite apenas letras, n√∫meros, "_", "-", "."
      const isLetter = (ch >= "a" && ch <= "z") || (ch >= "A" && ch <= "Z");
      const isNumber = ch >= "0" && ch <= "9";
      const isAllowed = ch === "_" || ch === "-" || ch === ".";

      if (isLetter || isNumber || isAllowed) {
        out += ch;
      } else {
        out += "_";
      }
    }
    return out;
  };

  const resolveDocFileName = (url?: string, fileName?: string) => {
    let base = (fileName && fileName.trim()) || "arquivo";

    // tenta pegar extens√£o pelo nome
    let ext = "";
    const lower = base.toLowerCase();

    const allowedExt = [
      ".pdf",
      ".doc",
      ".docx",
      ".xls",
      ".xlsx",
      ".zip",
      ".txt",
      ".csv",
    ];

    for (const e of allowedExt) {
      if (lower.endsWith(e)) {
        ext = e;
        break;
      }
    }

    // tenta pegar extens√£o pela URL
    if (!ext && url) {
      const urlLower = url.toLowerCase();
      for (const e of allowedExt) {
        if (urlLower.includes(e)) {
          ext = e;
          break;
        }
      }
    }

    // fallback
    if (!ext) ext = ".pdf";

    // limpeza sem regex
    base = cleanFileName(base);

    if (!base.toLowerCase().endsWith(ext)) base += ext;

    return { name: base, ext };
  };

  // ---------------------------------------------------------
  // --------- DOWNLOAD NATIVO (PDF, DOC, ZIP etc.) ----------
  // ---------------------------------------------------------
  const handleDownloadNative = async (url: string, fileName?: string) => {
    try {
      if (!url) {
        RN.Alert.alert("Arquivo inv√°lido", "URL n√£o encontrada.");
        return;
      }

      const { name } = resolveDocFileName(url, fileName);
      const baseDir = FileSystem.documentDirectory + "downloads_docs/";

      // cria pasta se n√£o existir
      const dirInfo = await FileSystem.getInfoAsync(baseDir);
      if (!dirInfo.exists) {
        await FileSystem.makeDirectoryAsync(baseDir, { intermediates: true });
      }

      const fileUri = baseDir + name;

      const result = await FileSystem.downloadAsync(url, fileUri);
      console.log("Download conclu√≠do:", result.uri);

      // abre share sheet (PDF, DOC, XLS etc.)
      if (await Sharing.isAvailableAsync()) {
        await Sharing.shareAsync(result.uri);
      } else {
        RN.Alert.alert("Download conclu√≠do", result.uri);
      }
    } catch (err) {
      console.log("Erro download:", err);
      RN.Alert.alert("Erro", "N√£o foi poss√≠vel baixar o documento.");
    }
  };

  // ---------------------------------------------------------
  // -------------------- DOWNLOAD WEB -----------------------
  // ---------------------------------------------------------
  const handleDownloadWeb = async (url: string, fileName?: string) => {
    if (!url) return;

    const { name } = resolveDocFileName(url, fileName);

    try {
      const proxy =
        "https://us-central1-flaxboll.cloudfunctions.net/proxyCall?url=";
      const resp = await fetch(proxy + url);
      if (!resp.ok) {
        throw new Error("HTTP " + resp.status);
      }

      const blob = await resp.blob();
      const blobUrl = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = blobUrl;
      link.download = name;

      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      URL.revokeObjectURL(blobUrl);
    } catch (e) {
      console.log("Erro web (fetch falhou, usando fallback <a>):", e);

      // Fallback: ainda tenta abrir/baixar usando um <a> simples
      const link = document.createElement("a");
      link.href = url;
      link.download = name;
      link.target = "_blank"; // se n√£o respeitar o download, pelo menos abre a aba
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  // ------------------- Handler √∫nico -----------------------
  const handlePress = (item: any) => {
    const url = item?.fileUrl || item?.receiptUrl;
    const name = item?.fileName;

    if (!url) return;

    if (RN.Platform.OS === "web") {
      handleDownloadWeb(url, name);
    } else {
      handleDownloadNative(url, name);
    }
  };

